import React, { useEffect, useState } from "react";
import axios from 'axios';
import queryString from "query-string";
import io  from 'socket.io-client';

let socket;
const Chat = () => {
    const username = 'woodly'
    const [chats, setChats] = useState([]);  
    const ENDPOINT = 'http://localhost:5000';


    const getChats = async () => {
        try {
            const response = await axios.post('http://localhost:5000/loadePrivateUserChats', {username: username});
            setChats(response.data); 

            // Hier nach dem Setzen der Chats die Verbindung herstellen
            socket = io(ENDPOINT);
            socket.emit('join', { username, ChatID: response.data[0]?.ChatID });

            socket.on('disconnect', () => {
                console.log('Disconnected from server');
                // Hier kannst du auf die Trennung reagieren, z.B. den Benutzer benachrichtigen.
            });
        } catch (err) {
            console.error(err);
        }
    };

    useEffect(() => {
        // fetchData aufrufen
        getChats();

        // Cleanup: Disconnect socket when component unmounts
        return () => {
            if (socket) {
                socket.disconnect();
            }
        };
    }, [ENDPOINT, username]);  // Kein Bedarf an `chats` hier

    return (
        <div>
            <h1>Hello Chat</h1>
            {chats.map(chat => (
                <div key={chat.ChatID}>
                    <p>Username: {chat.username}</p>
                    <p>ChatID: {chat.ChatID}</p>
                </div>
            ))}
        </div>
    );
}

export default Chat;
