import React, { useEffect, useState } from "react";
import axios from 'axios';
import queryString from "query-string";
import io  from 'socket.io-client';

export default function Chat(){
    const Chat  = ({ location }) => {

        const {chatID, steChatID} = useState('')

        useEffect(async ()  => {
            try {
                const response = await axios.post('http://localhost:5000/login', { username :'woodly' });
                steChatID(response.data);

            } catch (err) {
                console.error(err);
            }
        })

        return (
            <div className="main">
                <div className="wrapperr">
                    <div className="signin">
                        <div className="content">
                            <h2>Sign In</h2>
                            <p>{registerError}</p>
                            <div className="form">
                                <div className="inputBox">
                                    <input type="text" placeholder="Username" required value={name} onChange={e => setName(e.target.value)}/>
                                </div>
                                <div className="inputBox">
                                    <input type="text" placeholder="Email" required value={email} onChange={e => setEmail(e.target.value)}/>
                                </div>
                                <div className="inputBox">
                                    <input type="password" placeholder="Password" required value={password} onChange={e => setPassword(e.target.value)}/>
                                </div>
                                <div class="links"> <a href="#"></a> <a href="/login">Already got an account?</a> </div>
                                <div className="inputBox">
                                    <input type="submit" onClick={addUser} value="Create your account"/>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
}}
