import React, { useEffect, useState } from "react";
import axios from 'axios';
import queryString from "query-string";
import io  from 'socket.io-client';

let socket
const Chat  = () => {

    const [ChatID, setChaID] = useState('');  
    const getChatID = async () => {
            
        try {
            const response = await axios.post('http://localhost:5000/loadePrivateUserChats', {username: 'woodly'});
            
            setChaID(response.data.ChatID); // Korrigiere den Tippfehler
            
        } catch (err) {
            console.error(err);
        }
    };

    useEffect(() => {
        getChatID();
        socket = io();
    });


    return (
        <h1>Chat</h1>
    )
}

export default Chat;
